(323, 1, '2023-05-30 10:21:07', '2023-05-30 08:21:07', '{\n    \"hestia::hestia_slider_content\": {\n        \"value\": \"[{\\\"color\\\":\\\"\\\",\\\"color2\\\":\\\"\\\",\\\"text\\\":\\\"R\\u00e9servez votre s\\u00e9jour\\\",\\\"link\\\":\\\"\\\",\\\"text2\\\":\\\"\\\",\\\"link2\\\":\\\"\\\",\\\"image_url\\\":\\\"http://localhost/wordpress/wp-content/themes/hestia/assets/img/slider1.jpg\\\",\\\"title\\\":\\\"Camping Les Portes de la Baie de Somme\\\",\\\"subtitle\\\":\\\"\\\",\\\"social_repeater\\\":\\\"undefined\\\",\\\"id\\\":\\\"customizer_repeater_56d7ea7f40a56\\\",\\\"shortcode\\\":\\\"undefined\\\"}]\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2023-05-30 08:18:51\"\n    },\n    \"hestia::hestia_features_content\": {\n        \"value\": \"[{\\\"icon_value\\\":\\\"fas fa-home\\\",\\\"color\\\":\\\"#1bd64d\\\",\\\"text\\\":\\\"Le camping se situe dans un endroit calme et apaisant. Pratique pour se ressourcer et respirer du bon air.\\\",\\\"link\\\":\\\"#\\\",\\\"text2\\\":\\\"undefined\\\",\\\"title\\\":\\\"Naturel\\\",\\\"subtitle\\\":\\\"undefined\\\",\\\"social_repeater\\\":\\\"undefined\\\",\\\"id\\\":\\\"social-repeater-6470ccf7cdb8d\\\",\\\"shortcode\\\":\\\"undefined\\\"},{\\\"icon_value\\\":\\\"fas fa-check\\\",\\\"color\\\":\\\"#00bcd4\\\",\\\"text\\\":\\\"Nous faisons de notre mieux pour vous donner la meilleure exp\\u00e9rience possible au sein de notre companie.\\\",\\\"link\\\":\\\"#\\\",\\\"text2\\\":\\\"undefined\\\",\\\"title\\\":\\\"Qualit\\u00e9\\\",\\\"subtitle\\\":\\\"undefined\\\",\\\"social_repeater\\\":\\\"undefined\\\",\\\"id\\\":\\\"social-repeater-6470ccf7c2e42\\\",\\\"shortcode\\\":\\\"undefined\\\"},{\\\"icon_value\\\":\\\"far fa-life-ring\\\",\\\"color\\\":\\\"#4caf50\\\",\\\"text\\\":\\\"Des personnes \\u00e0 l&#39;\\u00e9coute de ses visiteurs et de leurs besoins.\\\",\\\"link\\\":\\\"#\\\",\\\"text2\\\":\\\"undefined\\\",\\\"title\\\":\\\"Support\\\",\\\"subtitle\\\":\\\"undefine